	b = append(b, []byte{}...)
	b = append(b, []byte{0x27, 0x61, 0x27}...)
	b = append(b, []byte{0x27, 0x68, 0x6f, 0x72, 0x6f, 0x27}...)
	b = append(b, []byte{0x27, 0x6b, 0x61, 0x6a, 0x27, 0x20}...)
