	b := make([]byte, 0, 32)

	b = strconv.AppendInt(b, int64(t.Day()), 10)
	b = append(b, []byte{0x20}...)
	b = append(b, gu.monthsAbbreviated[t.Month()]...)
	b = append(b, []byte{0x2c, 0x20}...)
